INSERT INTO `posts` (`id`, `title`, `body`, `created_at`, `updated_at`, `user_id`, `cover_image`, `category`) VALUES
(10, 'Hệ thống điện mặt trời hybrid 5kwp tại TT Lâm, Nam Định', '<p>Hệ thống điện mặt trời hybrid 5kwp&nbsp;thị trấn L&acirc;m, Nam Định. H&agrave;ng th&aacute;ng&nbsp;phần điện được h&ograve;a lưới&nbsp; v&agrave; dự trữ đ&atilde; gi&uacute;p chủ nh&agrave; giảm được đ&aacute;ng kể tiền điện.</p>', '2020-02-27 07:48:04', '2020-03-24 12:58:16', 2, '/uploaded_images/b37b04e1d95621087847_1582697685.jpg', 'writing'),
(11, 'Hệ Thống Điện Mặt Trời Hòa Lưới 3,3kwp tại Vĩnh Tuy, Hà Nội', '<p>Hệ thống được ch&uacute;ng t&ocirc;i lắp đặt v&agrave;o th&aacute;ng 10/2019. Từ đ&oacute; hệ thống đi v&agrave;o sử dụng rất ổn định với c&aacute;c thiết bị ch&uacute;ng t&ocirc;i đảm bảo chất lượng nhất. Hiệu suất của to&agrave;n hệ thống rất cao v&agrave; song song l&agrave; chi ph&iacute; tiền điện h&agrave;ng th&aacute;ng tại gia đ&igrave;nh giảm đi đ&aacute;ng kể. Chủ nh&agrave; chỉ phải chi trả một phần tiền điện so với h&agrave;ng th&aacute;ng trước đ&acirc;y. Chủ nh&agrave;&nbsp;rất h&agrave;i l&ograve;ng với hệ thống h&ograve;a lưới n&agrave;y.</p>', '2020-02-27 07:49:39', '2020-02-27 07:52:03', 2, '/uploaded_images/9d3ef1552ce2d4bc8df3_1582698723_1582789796.jpg', ''),
(13, 'Tiêu chí lựa chọn tấm pin năng lượng mặt trời', '<p>Hiện tại&nbsp;thị trường Việt Nam&nbsp;c&oacute; rất nhiều những h&atilde;ng pin mặt trời tham gia v&agrave;o lĩnh vực NLTT. Với những người mới&nbsp;muốn t&igrave;m hiểu về pin mặt trời th&igrave; c&oacute; thể chưa&nbsp;hiểu hết về nguy&ecirc;n l&yacute; hoạt động hay ti&ecirc;u ch&iacute; lựa chọn tấm pin tr&ecirc;n thị trường. B&agrave;i viết n&agrave;y t&ocirc;i sẽ tổng hợp lại theo dạng đầu mục ngắn gọn nhất ti&ecirc;u ch&iacute; lựa chọn tấm pin, c&ograve;n nguy&ecirc;n l&yacute; hoạt động tấm pin xin ph&eacute;p tr&igrave;nh b&agrave;y tại những b&agrave;i viết kh&aacute;c.</p>\r\n\r\n<p><em>Bảng ti&ecirc;u ch&iacute; lựa chọn tấm pin:</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellspacing=\"0\" style=\"width:517pt\">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan=\"2\" style=\"height:16.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\"><strong>STT</strong></td>\r\n			<td rowspan=\"2\" style=\"height:16.5pt; text-align:center; vertical-align:middle; white-space:normal; width:98pt\"><strong>Ti&ecirc;u ch&iacute;</strong></td>\r\n			<td rowspan=\"2\" style=\"height:16.5pt; text-align:center; vertical-align:middle; white-space:normal; width:194pt\"><strong>Diễn giải</strong></td>\r\n			<td colspan=\"4\" style=\"height:16.5pt; text-align:center; vertical-align:middle; white-space:normal; width:197pt\"><strong>H&atilde;ng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:33.0pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\"><strong>Canadian</strong></td>\r\n			<td style=\"height:33.0pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\"><strong>Power China</strong></td>\r\n			<td style=\"height:33.0pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\"><strong>GE</strong></td>\r\n			<td style=\"height:33.0pt; text-align:center; vertical-align:middle; white-space:normal; width:59pt\"><strong>TrinaSolar</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:135.0pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\"><strong>1</strong></td>\r\n			<td style=\"height:135.0pt; vertical-align:middle; white-space:normal; width:98pt\"><strong>Uy t&iacute;n nh&agrave; Sản xuất: Tier 1</strong></td>\r\n			<td style=\"height:135.0pt; vertical-align:middle; white-space:normal; width:194pt\"><strong>Tier -1 l&agrave; thang xếp hạng cao nhất về uy t&iacute;n độ tin cậy, chất lượng tấm pin (Th&acirc;m ni&ecirc;n tr&ecirc;n 5 năm, D&acirc;y truyền SX tự động, C&oacute; mảng RD,&hellip;). C&oacute; 3 thang tier 1,2,3. Tier 1 c&oacute; ti&ecirc;u ch&iacute; khắt khe v&agrave; cao nhất</strong></td>\r\n			<td style=\"height:135.0pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">tier 1</td>\r\n			<td style=\"height:135.0pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">x</td>\r\n			<td style=\"height:135.0pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">x</td>\r\n			<td style=\"height:135.0pt; vertical-align:middle; white-space:normal; width:59pt\">tier 1</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:61.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\"><strong>2</strong></td>\r\n			<td style=\"height:61.5pt; vertical-align:middle; white-space:normal; width:98pt\"><strong>Th&ocirc;ng số kỹ thuật của tấm pin</strong></td>\r\n			<td style=\"height:61.5pt; vertical-align:middle; white-space:normal; width:194pt\"><strong>&nbsp;</strong></td>\r\n			<td colspan=\"4\" style=\"height:61.5pt; text-align:center; vertical-align:middle; white-space:normal; width:197pt\">chung 1 c&ocirc;ng suất.</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:38.25pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">2.1</td>\r\n			<td style=\"height:38.25pt; vertical-align:middle; white-space:normal; width:98pt\">Pmax (CS đỉnh)</td>\r\n			<td style=\"height:38.25pt; vertical-align:middle; white-space:normal; width:194pt\">C&ocirc;ng suất đỉnh của tấm pin trong điều kiện STC (c&agrave;ng cao c&agrave;ng tốt)</td>\r\n			<td style=\"height:38.25pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">340w</td>\r\n			<td style=\"height:38.25pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:38.25pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:38.25pt; vertical-align:middle; white-space:normal; width:59pt\">340w</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:218.25pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">2.2</td>\r\n			<td style=\"height:218.25pt; vertical-align:middle; white-space:normal; width:98pt\">Module Efficiency (Hiệu suất chuyển đổi của Module)</td>\r\n			<td style=\"height:218.25pt; vertical-align:middle; white-space:normal; width:194pt\">Hiệu suất tấm pin được t&iacute;nh bằng c&aacute;ch lấy Pmax chia cho diện t&iacute;ch tấm pin. gi&aacute; trị n&agrave;y c&agrave;ng cao c&agrave;ng tốt;<br />\r\n			Bản chất của hiệu suất chuyển đổi l&agrave; tỉ lệ năng lượng chuyển ho&aacute; th&agrave;nh điện năng v&agrave; năng lượng ban đầu của photon, 1 phần lớn năng lượng dư thừa mất đi chuyển ho&aacute; th&agrave;nh nhiệt v&agrave; hiệu suất chuyển đổi của pv thường khoảng 20% ;</td>\r\n			<td style=\"height:218.25pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">19.4%</td>\r\n			<td style=\"height:218.25pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:218.25pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:218.25pt; vertical-align:middle; white-space:normal; width:59pt\">17.5%</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:58.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">2.3</td>\r\n			<td style=\"height:58.5pt; vertical-align:middle; white-space:normal; width:98pt\">Power Tolerance (Sai số c&ocirc;ng suất)</td>\r\n			<td style=\"height:58.5pt; vertical-align:middle; white-space:normal; width:194pt\">Phản &aacute;nh tỷ lệ sai số về c&ocirc;ng suất của tấm pin, c&agrave;ng nhỏ c&agrave;ng tốt</td>\r\n			<td style=\"height:58.5pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">&plusmn;5W</td>\r\n			<td style=\"height:58.5pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:58.5pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:58.5pt; vertical-align:middle; white-space:normal; width:59pt\">&plusmn;5W</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:124.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">2.4</td>\r\n			<td style=\"height:124.5pt; vertical-align:middle; white-space:normal; width:98pt\">Temperature coefficients of Pmax (Độ suy giảm CS theo nhiệt độ)</td>\r\n			<td style=\"height:124.5pt; vertical-align:middle; white-space:normal; width:194pt\">Th&ocirc;ng số n&agrave;y cho biết tỷ lệ suy giảm C&ocirc;ng suất ph&aacute;t điện của tấm pin khi nhiệt độ m&ocirc;i trường tăng (tỷ lệ c&agrave;ng thấp c&agrave;ng tốt)<br />\r\n			do nhiệt độ tăng dẫn đến điện trở vật liệu tăng n&ecirc;n c&ocirc;ng suất giảm.</td>\r\n			<td style=\"height:124.5pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">(-0.37%/K)</td>\r\n			<td style=\"height:124.5pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:124.5pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:124.5pt; vertical-align:middle; white-space:normal; width:59pt\">(-0.39%/K)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:110.25pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">2.5</td>\r\n			<td style=\"height:110.25pt; vertical-align:middle; white-space:normal; width:98pt\">Nominal operating cell temperature (NOCT - nhiệt độ hoạt động của Cell)</td>\r\n			<td style=\"height:110.25pt; vertical-align:middle; white-space:normal; width:194pt\">Dải nhiệt độ hoạt động của Cell c&agrave;ng cao c&agrave;ng tốt (Nhiệt độ hoạt động của Cell &gt;= nhiệt độ m&ocirc;i trường +15 l&agrave; tốt)</td>\r\n			<td style=\"height:110.25pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">44 độ C</td>\r\n			<td style=\"height:110.25pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:110.25pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:110.25pt; vertical-align:middle; white-space:normal; width:59pt\">44 độ C</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:32.25pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\"><strong>3</strong></td>\r\n			<td style=\"height:32.25pt; vertical-align:middle; white-space:normal; width:98pt\"><strong>C&ocirc;ng nghệ sản suất/duy tr&igrave; hiệu xuất</strong></td>\r\n			<td style=\"height:32.25pt; vertical-align:middle; white-space:normal; width:194pt\"><strong>&nbsp;</strong></td>\r\n			<td style=\"height:32.25pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\"><strong>&nbsp;</strong></td>\r\n			<td style=\"height:32.25pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\"><strong>&nbsp;</strong></td>\r\n			<td style=\"height:32.25pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\"><strong>&nbsp;</strong></td>\r\n			<td style=\"height:32.25pt; vertical-align:middle; white-space:normal; width:59pt\"><strong>&nbsp;</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:94.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">3.1</td>\r\n			<td style=\"height:94.5pt; vertical-align:middle; white-space:normal; width:98pt\"><strong>C&ocirc;ng nghệ Haff-cut Cell</strong></td>\r\n			<td style=\"height:94.5pt; vertical-align:middle; white-space:normal; width:194pt\">Half-cut cell sử dụng phương ph&aacute;p cắt cell &nbsp;truyền thống ra hai phần bằng nhau th&ocirc;ng qua tia laser. Kỹ thuật n&agrave;y gi&uacute;p tăng một phần hiệu suất so với tấm pin truyền thống</td>\r\n			<td style=\"height:94.5pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">YC</td>\r\n			<td style=\"height:94.5pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">YC</td>\r\n			<td style=\"height:94.5pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">YC</td>\r\n			<td style=\"height:94.5pt; vertical-align:middle; white-space:normal; width:59pt\">YC</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:211.5pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">3.2</td>\r\n			<td style=\"height:211.5pt; vertical-align:middle; white-space:normal; width:98pt\">LID (Light Induced Degradation - Độ suy giảm cảm ứng &aacute;nh s&aacute;ng)</td>\r\n			<td style=\"height:211.5pt; vertical-align:middle; white-space:normal; width:194pt\">LID l&agrave; sự mất hiệu suất của c&aacute;c tấm pin silicon (c&oacute; giải ph&aacute;p chống suy giảm)<br />\r\n			do photon t&aacute;c động v&agrave;o c&aacute;c ph&acirc;n tử oxy trong cell, ch&uacute;ng bị t&iacute;ch điện dương v&agrave; c&oacute; mức năng lượng mới cao hơn n&ecirc;n ch&uacute;ng c&oacute; khả năng h&uacute;t c&aacute;c e thậm ch&iacute; cả lỗ trống ở ph&iacute;a dưới n&ecirc;n g&acirc;y giảm hiệu suất gọi l&agrave; LID</td>\r\n			<td style=\"height:211.5pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">&nbsp;</td>\r\n			<td style=\"height:211.5pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:211.5pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:211.5pt; vertical-align:middle; white-space:normal; width:59pt\">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:227.25pt; text-align:center; vertical-align:middle; white-space:normal; width:28pt\">3.3</td>\r\n			<td style=\"height:227.25pt; vertical-align:middle; white-space:normal; width:98pt\">PID (Potential Induced Dagradation - Suy giảm hiệu suất hệ thống)&nbsp;</td>\r\n			<td style=\"height:227.25pt; vertical-align:middle; white-space:normal; width:194pt\">PID chủ yếu xảy ra ở c&aacute;c tấm pin gần cực &acirc;m của 1 string. Do sự ch&ecirc;nh lệch điện &aacute;p cao so với khung tấm pin ( khung nối đất) g&acirc;y ra điện trường l&agrave;m c&aacute;c ion dương từ tấm k&iacute;nh cường lực đi v&agrave;o cell, ion n&agrave;y g&acirc;y ăn m&ograve;n điện ho&aacute; tr&ecirc;n cell c&ugrave;ng với n&oacute; l&agrave; c&aacute;c e trong tấm pin c&oacute; thể đi ra ngo&agrave;i g&acirc;y n&ecirc;n hiện tượng PID. đ&acirc;y l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y giảm hiệu suất pv.</td>\r\n			<td style=\"height:227.25pt; text-align:center; vertical-align:middle; white-space:normal; width:43pt\">kh&aacute;ng PID</td>\r\n			<td style=\"height:227.25pt; text-align:center; vertical-align:middle; white-space:normal; width:47pt\">&nbsp;</td>\r\n			<td style=\"height:227.25pt; text-align:center; vertical-align:middle; white-space:normal; width:48pt\">&nbsp;</td>\r\n			<td style=\"height:227.25pt; vertical-align:middle; white-space:normal; width:59pt\">&nbsp;kh&aacute;ng PID</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện tại t&ocirc;i ưu ti&ecirc;n lựa d&ugrave;ng những tấm pin c&oacute; c&ocirc;ng nghệ hafl cut cell v&agrave; uy t&iacute;n nh&agrave; sản xuất hơn cả./.</p>\r\n\r\n<p>&nbsp;</p>', '2020-02-29 01:55:37', '2020-04-01 08:35:46', 2, '/uploaded_images/shutterstock-534806539-1490355176741_1582941504.jpg', 'solution');
